mapper.select:
select EPISODE, nvId, EP_NUM, EP_TITLE, EP_CONTENT, IS_PURCHASED
from novel_episode_tb
where nvId = #{nvId}

select EPISODE, nvId, EP_NUM, EP_TITLE, EP_CONTENT, IS_PURCHASED
from novel_episode_tb
where nvId = #{nvId} and EPISODE = #{EPISODE}

mapper.update:
update novel_episode_tb 
set IS_PURCHASED = #{isPurchased}  
where nvId = #{nvId} and EPISODE = #{EPISODE}
